import UIKit

protocol ListContactCleanBusinessLogic {
  func load()
}

protocol ListContactCleanDataStore {
  //var name: String { get set }
}

class ListContactCleanInteractor: ListContactCleanBusinessLogic, ListContactCleanDataStore {
    
    var presenter: ListContactCleanPresentationLogic?
    var worker: ListContactCleanWorker?
    
    init(worker: ListContactCleanWorker = ListContactCleanWorker()) {
      self.worker = worker
    }
    
    func load() {
        presenter?.startLoading()
        worker?.getData().done(handleSuccess).catch(handleFailure)
    }
    
    func handleSuccess(_ response: [Contact]) {
        presenter?.loadList(response)
        presenter?.stopLoading()
    }
    
    func handleFailure(_ error: Error) {
        presenter?.showError()
    }

    
}
