import UIKit

protocol ListContactCleanPresentationLogic {
    func startLoading()
    func stopLoading()
    func loadList(_ listContact: [Contact])
    func showError()
}

class ListContactCleanPresenter: ListContactCleanPresentationLogic {
    
    weak var viewController: ListContactCleanDisplayLogic?
    
    func loadList(_ listContact: [Contact]) {
        let viewModel: ListContactCleanViewModel = ListContactCleanViewModel(listContact)
        viewController?.loadList(viewModel: viewModel)
    }
    
    func showError() {
        viewController?.showError()
    }
    
    func startLoading() {
        viewController?.startLoading()
    }
    
    func stopLoading() {
        viewController?.stopLoading()
    }
  
}
